package sum_of_n_Number;

public class Sumofno {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i, num = 10, sum = 0;  
		 
		for(i = 1; i <= num; i++) {    
		sum = sum + i;  
		}  
		//prints the sum   
		System.out.println("Sum of First 10 Natural Numbers is = " + sum);
	}
}
